import React, { useState } from "react";

import EmailAddressSignUpTextField, {
  CreateAccountButtonSignUp,
  FirstNameSignUpTextField,
  PasswordSignUpTextField,
  LastNameSignUpTextField,
} from "./SignUpComponents.jsx";

const mainDivStyle = {
  width: "500px",
  alignItems: "center",
  padding: "40px",
};

function SignUpForm() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [emailAddress, setEmailAddress] = useState("");
  const [password, setPassword] = useState("");

  const onFirstNameChange = (e) => {
      setFirstName(e.target.value);
  };

  const onLastNameChange = (e) => {
      setLastName(e.target.value);
  };

  const onEmailAddressChange = (e) => {
      setEmailAddress(e.target.value);
  };

  const onPasswordChange = (e) => {
      setPassword(e.target.value);
  };

  const SignUp = async (event) => {
      event.preventDefault();

      var signUpJson = {
          firstName,
          lastName,
          emailAddress,
          password
      };

      try {
          await fetch('/api/register', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify(signUpJson),
          });
          console.log("sent")
          // ...
      } catch (error) {
          console.log(error)
      }
  };

  
  

  return (
    <form style={mainDivStyle}>
      <p style={{ fontSize: "30px" }}>
        <b>Sign Up to ServiceHub</b>
      </p>

      <span style={{ padding: "60px" }} />
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          width: "100%",
        }}
      >
        <FirstNameSignUpTextField onFirstNameChange={(e) => onFirstNameChange(e)}/>

        <LastNameSignUpTextField onLastNameChange = {(e) => onLastNameChange(e)}/>
      </div>

      <EmailAddressSignUpTextField
        error="false"
        onEmailChange={onEmailAddressChange}
      />

      <PasswordSignUpTextField onPasswordChange={(e) => onPasswordChange(e)} />

      <CreateAccountButtonSignUp signUpClicked={SignUp} />

      <p style={{ margin: "20px 0px 0px 0px" }}>
        Already have an account?{" "}
        <a href="3" style={{ color: "green" }}>
          Log In
        </a>
      </p>
    </form>
  );
}

export default SignUpForm;
