import React, { useState } from "react";
import EmailAddressSignUpTextField, {
  CreateAccountButtonSignUp,
  FirstNameSignUpTextField,
  PasswordSignUpTextField,
  LastNameSignUpTextField,
} from "./SignUpComponents.jsx";

const mainDivStyle = {
  width: "500px",
  alignItems: "center",
  padding: "40px",
};

function SignUpForm() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [emailAddress, setEmailAddress] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({});

  const [passwordHasError, setPasswordHasError] = useState(false);

  const validateForm = () => {
    const newErrors = {};
    if (!firstName) {
      newErrors.firstName = "First name is required";
    }
    if (!lastName) {
      newErrors.lastName = "Last name is required";
    }
    if (!emailAddress) {
      newErrors.emailAddress = "Email address is required";
    }
    if (!password) {
      newErrors.password = "Password is required";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const SignUp = async (event) => {
    event.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    const signUpJson = {
      firstName,
      lastName,
      emailAddress,
      password,
    };

    try {
      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(signUpJson),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setPasswordHasError(true)
        setErrors(errorData)
        console.log('Error response from server:', errorData);
        console.log(errorData[0])
      }else{
        setPasswordHasError(false)
      }

      const data = await response.json();
      console.log('Success:', data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <form style={mainDivStyle} onSubmit={SignUp}>
      <p style={{ fontSize: "30px" }}>
        <b>Sign Up to ServiceHub</b>
      </p>

      <span style={{ padding: "60px" }} />
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          width: "100%",
        }}
      >
        <FirstNameSignUpTextField onFirstNameChange={(e) => setFirstName(e.target.value)} />

        <LastNameSignUpTextField onLastNameChange={(e) => setLastName(e.target.value)} />
      </div>

      <EmailAddressSignUpTextField
        error="false"
        onEmailChange={(e) => setEmailAddress(e.target.value)}
      />

      <PasswordSignUpTextField onPasswordChange={(e) => setPassword(e.target.value)} error={passwordHasError} errorMessage={errors[0]} />

      <CreateAccountButtonSignUp/>

      <p style={{ margin: "20px 0px 0px 0px" }}>
        Already have an account?{" "}
        <a href="3" style={{ color: "green" }}>
          Log In
        </a>
      </p>
    </form>
  );
}

export default SignUpForm;
