//Header Styles
const logoStyle = {
    textAlign: "start",
    color: "green",
    fontSize: "20px",
    padding: "0px 0px 0px 10px",
};

//Optional Div Style
const optionalDivStyle = {
    width: "65px",
    backgroundColor: "gray",
    height: "0.5px",
};
  
const optionalMainDivStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
};

//RoleOptionButton Style
const imageStyle = {
    width: "40px",
    height: "40px",
};
  
const buttonDivStyle = {
    width: "300px",
    height: "180px",
    border: "2px solid gray",
    borderRadius: "15px",
    margin: "20px",
    cursor: "pointer", // Add pointer cursor for better UX
};
  
const buttonSelectedDivStyle = {
    ...buttonDivStyle,
    border: "2px solid green",
};
  
const topDivStyle = {
    position: "relative",
    display: "flex",
    width: "100%",
    height: "35%",
    justifyContent: "space-between",
    padding: "0px 10px",
    paddingTop: "20px",
    boxSizing: "border-box",
    alignItems: "center",
};
  
const paragraphStyle = {
    fontSize: "20px",
    padding: "0px 20px",
    textAlign: "start",
};
  
const bottomDiv = {
    height: "60%",
    alignItems: "center",
    display: "flex",
};


//TextFields Styles
const textFieldStyles = {
  width: "300px",
  padding: "10px",
  height: "8x",
  borderRadius: "10px",
  border: "2px solid gray",
  margin: "10px",
};

const textFieldErrorStyles = {
  width: "300px",
  padding: "10px",
  height: "8x",
  borderRadius: "10px",
  border: "2px solid red",
  margin: "10px",
};


export default optionalDivStyle;

export {optionalMainDivStyle, logoStyle}

export{imageStyle, buttonDivStyle, buttonSelectedDivStyle, topDivStyle, paragraphStyle, bottomDiv}

export{textFieldErrorStyles, textFieldStyles}
