// styles.js

// Header Styles
export const logoStyle = {
    textAlign: "start",
    color: "green",
    fontSize: "20px",
    padding: "0px 0px 0px 10px",
};

// Optional Div Style
export const optionalDivStyle = {
    width: "65px",
    backgroundColor: "gray",
    height: "0.5px",
};
  
export const optionalMainDivStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
};

// RoleOptionButton Style
export const imageStyle = {
    width: "40px",
    height: "40px",
};
  
export const buttonDivStyle = {
    width: "300px",
    height: "180px",
    border: "2px solid gray",
    borderRadius: "15px",
    margin: "20px",
    cursor: "pointer", // Add pointer cursor for better UX
};
  
export const buttonSelectedDivStyle = {
    ...buttonDivStyle,
    border: "2px solid green",
};
  
export const topDivStyle = {
    position: "relative",
    display: "flex",
    width: "100%",
    height: "35%",
    justifyContent: "space-between",
    padding: "0px 10px",
    paddingTop: "20px",
    boxSizing: "border-box",
    alignItems: "center",
};
  
export const paragraphStyle = {
    fontSize: "20px",
    padding: "0px 20px",
    textAlign: "start",
};
  
export const bottomDiv = {
    height: "60%",
    alignItems: "center",
    display: "flex",
};

// TextFields Styles
export const textFieldStyles = {
    width: "300px",
    padding: "10px",
    height: "8x",
    borderRadius: "10px",
    border: "2px solid gray",
    margin: "10px",
};

export const textFieldErrorStyles = {
    width: "300px",
    padding: "10px",
    height: "8x",
    borderRadius: "10px",
    border: "2px solid red",
    margin: "10px",
};

export const CreateAccountWhiteBgStyle = {
    color: "green",
    width: "320px",
    borderRadius: "15px",
    padding: "10px",
    backgroundColor: "white",
    border: "2px solid green",
    // transition: "backgroundColor 0.3s ease, boxShadow 0.3s ease, transform 0.3s ease"
};

export const signInButtonHover = {
    filter: "brightness(0.8)"
}

export const CreateAccountWhiteBgStyleHover = {
    filter: "brightness(0.8)"
}


export const signInButton = {
    color: "white",
    width: "320px",
    borderRadius: "15px",
    padding: "10px",
    backgroundColor: "green",
    border: "1px solid green",
};

export const divStyle = {
    width: "65px",
    backgroundColor: "gray",
    height: "0.5px",
};

export const mainDivStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
};
