import React, { useState } from "react";
import OptionalDiv, { PasswordTextFieldInput, EmailTextFieldInput, SignInButton, CreateAccountWhiteBg} from "./SignInComponents";



const mainDivStyle = {
  width: "500px",
  alignItems: "center",
  justifyContent: "center",
  padding: "40px",
  border: "1px solid gray",
  borderRadius: "20px",
};

function SignInForm() {
  const [emailAddress, onEmailAddressChange] = useState("");
  const [userPassword, onPasswordChange] = useState("");

  const [emailHasError, onEmailErrorChange] = useState(false);
  const [passwordHasError, onPasswordErrorChange] = useState(false);

  const [emailErrorMessage, onEmailErrorMessage] = useState(false);
  const [passwordErrorMessage, onPasswordErrorMessage] = useState(false);
  

  const signIn = async(event) => {
    event.preventDefault();

    const signInJson = {
      emailAddress,
      userPassword
    }

    try{
      const response = await fetch("http://localhost:5000/auth/signIn", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },

      body: JSON.stringify(signInJson)
    })

    if(!response.ok){
      const errorMessage = await response.json();
      console.log(errorMessage)

      if(errorMessage.type === "email"){
        onEmailErrorMessage(errorMessage.msg)
        onEmailErrorChange(true)
      }else{
        onPasswordErrorMessage(errorMessage.msg)
        onPasswordErrorChange(true)
      }
    }else{
      onEmailErrorChange(false)
      onPasswordErrorChange(false)
    }
    }catch(error){
      console.log(error)
      onEmailErrorChange(false)
      onPasswordErrorChange(false)
      
    }


  }
  return (
    <form style={mainDivStyle} onSubmit={signIn}>
      
      <p style={{ fontSize: "25px" }}>
        <b>Sign In to ServiceHub</b>
      </p>

      <EmailTextFieldInput error = {emailHasError} 
      onEmailAddressChange = {(e) => onchange(e.target.value)} 
      errorMessage= {emailErrorMessage} />

      <PasswordTextFieldInput error = {passwordHasError} onPasswordChange = {(e) => onPasswordChange(e.target.value)} errorMessage={passwordErrorMessage}/>

      <div
        style={{
          width: "410px",
          display: "flex",
          justifyContent: "flex-end",
          padding: "0px 0px 5px 0px",
        }}
      >
        <a href="" style={{color: "green"}}>Forgot Password</a>
      </div>

      <span style={{ padding: "30px" }}></span>

      <SignInButton/>

      <OptionalDiv />

      <span style={{ padding: "30px" }}></span>

      <CreateAccountWhiteBg />

      <span style={{ padding: "30px" }}></span>
    </form>
  );
}

export default SignInForm;
