import React, { useState } from "react";
import OptionalDiv, { PasswordTextFieldInput, EmailTextFieldInput, SignInButton, CreateAccountWhiteBg} from "./SignInComponents";



const mainDivStyle = {
  width: "500px",
  alignItems: "center",
  justifyContent: "center",
  padding: "40px",
  border: "1px solid gray",
  borderRadius: "20px",
};

function SignInForm() {
  const [emailAddress, onEmailAddressChange] = useState("");
  const [userPassword, onPasswordChange] = useState("");
  

  const signIn = async(event) => {
    event.preventDefault();

    const signInJson = {
      emailAddress,
      userPassword
    }

    const response = await fetch("http://localhost:5000/auth/signIn", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },

      body: JSON.stringify(signInJson)
    })


  }
  return (
    <form style={mainDivStyle}>
      
      <p style={{ fontSize: "25px" }}>
        <b>Sign In to ServiceHub</b>
      </p>

      <EmailTextFieldInput error = {false} errorMessage = "" />

      <PasswordTextFieldInput error = {false} errorMessage = ""/>

      <div
        style={{
          width: "410px",
          display: "flex",
          justifyContent: "flex-end",
          padding: "0px 0px 5px 0px",
        }}
      >
        <a href="" style={{color: "green"}}>Forgot Password</a>
      </div>

      <span style={{ padding: "30px" }}></span>

      <SignInButton />

      <OptionalDiv />

      <span style={{ padding: "30px" }}></span>

      <CreateAccountWhiteBg />

      <span style={{ padding: "30px" }}></span>
    </form>
  );
}

export default SignInForm;
